from django.utils.unittest import TestCase # This is django's copy of the unittest included in python 2.7from django.test.client import Client#from django.test import TestCase # Has stuff like assertContains, would like to use, but is giving errors when includedclass TestUpload(TestCase):        def setUp(self):        self.c = Client()            def testHomePage(self):        "Test the home page exists"        response = self.c.get('/', {})        self.assertEqual(response.status_code, 200)